#{item}
group by period_date

select eqp_id eqpId, max(period_date) periodDate, round(sum(run_time)/3600,2) runTime, round(sum(idle_time)/3600,2) idleTime, round(sum(down_time)/3600,2) downTime, round(sum(pm_time)/3600,2) pmTime, round(sum(other_time)/3600,2) otherTime from rpt_eqp_state_day where period_date between #{beginTime} and #{endTime} and eqp_id in
#{item}
group by eqp_id

SELECT f.${groupName} groupName,
max(period_date) periodDate,
sum(run_time) runTime,
sum(idle_time) idleTime,
sum(down_time) downTime,
sum(pm_time) pmTime,
sum(other_time) otherTime
FROM
rpt_eqp_state_day t
LEFT JOIN fab_equipment f on t.EQP_ID=f.EQP_ID and f.del_flag=0 and f.active_flag = '1'
WHERE t.period_date between #{beginTime} and #{endTime}

    and f.step_code = #{officeId}


    and f.line_no = #{lineNo}


    and f.fab = #{fab}

group by f.${groupName}

SELECT period_date periodDate, sum(run_time) runTime, sum(idle_time) idleTime, sum(down_time) downTime, sum(pm_time) pmTime, sum(other_time) otherTime FROM rpt_eqp_state_day t LEFT JOIN fab_equipment f on t.EQP_ID=f.EQP_ID and f.del_flag=0 and f.active_flag = '1' WHERE t.period_date between #{beginTime} and #{endTime}
and f.OFFICE_ID = #{officeId}
and f.line_no = #{lineNo}
and f.fab = #{fab}
group by t.period_date

SELECT period_date periodDate, sum(run_time) runTime, sum(idle_time) idleTime, sum(down_time) downTime, sum(pm_time) pmTime, sum(other_time) otherTime, sum(run_time)+sum(idle_time)+sum(down_time)+sum(pm_time)+sum(other_time) as "totalTime" FROM rpt_eqp_state_day t LEFT JOIN fab_equipment f on t.EQP_ID=f.EQP_ID and f.del_flag=0 and f.active_flag = '1' WHERE t.period_date between #{beginTime} and #{endTime}
and f.fab = #{fab}
group by t.period_date
