mapper.update:
update FAB_EQUIPMENT_STATUS set  update_date = now()

     , EQP_STATUS = #{status}


     , lot_no = #{lotNo}


     , recipe_code = #{recipeCode}

where EQP_ID = #{eqpID}

update FAB_EQUIPMENT_STATUS set  update_date = now()

    , EQP_STATUS = #{status}


    , lot_no = #{lotNo}


    , recipe_code = #{recipeCode}


    , lot_yield = #{lotYield}


    , day_yield = #{dayYield}

where EQP_ID = #{eqpID}

mapper.select:
SELECT
, f.MODEL_NAME modelName
FROM
fab_equipment_status t
LEFT JOIN fab_equipment f on t.EQP_ID=f.EQP_ID and f.del_flag=0
WHERE t.del_flag = '0' and f.active_flag = '1'

    and f.OFFICE_ID = #{officeId}


    and f.line_no = #{lineNo}


    and f.fab = #{fab}

order by f.SORT_CODE
