mapper.select:
select t.*
from ms_measure_record r left join ms_measure_record_detail t on t.ms_record_id = r.id
where r.eqp_id = #{eqpId}
  and r.create_date between #{beginTime} and #{endTime}
order by t.create_date, t.ms_record_id

select t.*
from ms_measure_record r left join ms_measure_record_detail t on t.ms_record_id = r.id
where r.eqp_id = #{eqpId}
  and r.create_date between #{beginTime} and #{endTime}
and r.production_no = #{productionNo}
order by t.create_date, t.ms_record_id

SELECT CAST(min(dtl.item_value)*1000 AS DECIMAL(10,2)) as "minWeight", CAST(max(dtl.item_value)*1000 AS DECIMAL(10,2)) as "maxWeight", CAST(avg(dtl.item_value*1000) AS DECIMAL(10,2)) as "avgWeight", CAST(min(dtl.limit_min)*1000 AS DECIMAL(10,2)) as "limitMin", CAST(min(dtl.limit_max)*1000 AS DECIMAL(10,2)) as "limitMax", CAST(((min(dtl.limit_min)+min(dtl.limit_max))/2+(min(dtl.limit_max)-min(dtl.limit_min))/2*0.9)*1000 AS DECIMAL(10,2)) as "limit90", CAST(((min(dtl.limit_min)+min(dtl.limit_max))/2-(min(dtl.limit_max)-min(dtl.limit_min))/2*0.9)*1000 AS DECIMAL(10,2)) as "limit11", rcd.lot_no as "lotNo" from ms_measure_record_detail dtl LEFT JOIN ms_measure_record rcd on rcd.id=dtl.ms_record_id where dtl.item_code='WEIGHT'
and rcd.lot_no=#{lotNo}
and dtl.create_date > #{startTime}
and dtl.create_date < #{endTime}
and rcd.production_name=#{productionNo}
and row_name=#{detailOption}
and dtl.item_value between dtl.limit_min and dtl.limit_max GROUP BY rcd.lot_no order by dtl.create_date

select eqp_id as "eqpId",eqp_name as "eqpName"
  from fab_equipment
 where sub_line_no=#{lineNo}

SELECT IFNULL(COUNT(1),0)
   from ms_measure_record_detail dtl
   LEFT JOIN ms_measure_record rcd on rcd.id=dtl.ms_record_id
   where dtl.item_code='WEIGHT'
    and rcd.eqp_id=#{eqpId}
    and rcd.production_no=#{productionNo}
    and rcd.lot_no=#{lotNo}
    and dtl.row_name=#{rowName}
    and dtl.item_value = #{itemValue}
