mapper.select:
SELECT t.EQP_ID eqpId, t.LOT_ID lotId, t.EQP_STATUS eqpStatus, t.RECIPE_NAME recipeName, f.MODEL_NAME modelName FROM fab_equipment_status t left join fab_equipment f on t.EQP_ID=f.EQP_ID and f.del_flag=0 WHERE t.EQP_ID IN ( SELECT eqp_id FROM FAB_EQUIPMENT WHERE del_flag = '0' and active_flag = 'Y'
and OFFICE_ID = #{officeId}
) and t.del_flag = '0' order by f.SORT_NO

select count(*)
from ovn_batch_lot r left join ovn_batch_lot_param t on t.batch_id = r.id
where r.eqp_id = #{eqpId}
  and r.end_time between #{beginTime} and #{endTime}
order by t.create_date, t.batch_id

select t.temp_pv,
       t.temp_sp,
           t.temp_min,
           t.temp_max,t.other_temps_value,t.create_date
from ovn_batch_lot r left join ovn_batch_lot_param t on t.batch_id = r.id
where r.eqp_id = #{eqpId}
  and (
        (r.end_time > #{beginTime}
        AND r.end_time < #{endTime})
        OR
        (r.start_time > #{beginTime}
        AND r.start_time < #{endTime})
      )
  and t.create_date between #{beginTime} and #{endTime}
order by t.create_date, t.batch_id
