mapper.select:
     select #{periodDate} as periodDate,#{eqpId} as eqpId,#{eqpTemp} as eqpTemp,max(a.temp_pv) as tempMax,min(a.temp_pv) as tempMin,
      (select temp_pv from ovn_batch_lot_param where batch_id in (select id from ovn_batch_lot where eqp_id = #{eqpId} and ((end_time > #{startTime}
AND end_time < #{endTime}) OR (start_time > #{startTime} AND start_time < #{endTime})))
 and create_date between #{startTime} and #{endTime} and create_date = (select min(create_date) from ovn_batch_lot_param where batch_id in (select id from ovn_batch_lot where eqp_id = #{eqpId} and ((end_time > #{startTime}
AND end_time < #{endTime}) OR (start_time > #{startTime} AND start_time < #{endTime})))
and create_date between #{startTime} and #{endTime}) limit 1 ) as tempStart,
      (select temp_pv from ovn_batch_lot_param where batch_id in (select id from ovn_batch_lot where eqp_id = #{eqpId} and ((end_time > #{startTime}
AND end_time < #{endTime}) OR (start_time > #{startTime} AND start_time < #{endTime})))
 and create_date between #{startTime} and #{endTime} and create_date = (select max(create_date) from ovn_batch_lot_param where batch_id in (select id from ovn_batch_lot where eqp_id = #{eqpId} and ((end_time > #{startTime}
AND end_time < #{endTime}) OR (start_time > #{startTime} AND start_time < #{endTime})))
 and create_date between #{startTime} and #{endTime}) limit 1
 ) as tempEnd   from ovn_batch_lot_param a
      where a.batch_id in (select id from ovn_batch_lot where eqp_id = #{eqpId} and ((end_time > #{startTime}
AND end_time < #{endTime}) OR (start_time > #{startTime} AND start_time < #{endTime})))
 and a.create_date between #{startTime} and #{endTime}
